import axios from 'axios';
import React, { useState } from 'react'

function GetOneExam() {

    const [examnumber, setExamnumber] = useState("");
    const [loading, setLoading] = useState(false);
    const [msg, setMsg] = useState("");

    function handleFind({e, exam_number}){
        e.preventDefault();
        console.log(examnumber)

        axios.get(`https://morning-gorge-09623.herokuapp.com/api/v1/exam/${exam_number}`, {
            crossDomain: true,
            // withCredentials: true,
            headers: {
              'accept': 'application/json',
              "Content-type": "application/json;charset=UTF-8",
              'Access-Control-Allow-Origin': 'http://localhost:3000/',
              'Access-Control-Allow-Methods': 'GET',
              'Access-Control-Allow-Headers': 'Content-Type',
          },
          auth: {
            username: 'mujtaba@gmail.com',
            password: '123456'
          }
        })
            .then(res => {
                console.log(res);
                alert(`${exam_number} Deleted Successfully`)
                window.location.reload(true);
                setLoading(false);
                console.log(res);
            })
            .catch((error)=>{
                setLoading(false)
                setMsg(error.response.data.error);
                console.error(error)
                console.log(error.message);
            });  


    }
  return (
    <div className='container'>
        <h3>Get Single Exam by Unique ID</h3>

        <form onSubmit={handleFind}>
            <label>Exam Number:
                <input type='text'
                    placeholder='Exam Number' 
                    value={examnumber.toUpperCase()}
                    onChange={(e)=>setExamnumber(e.target.value)}
                 />

            <button>Find</button>
            </label>
        </form>

    </div>

  )
}

export default GetOneExam
