import React, { useState } from 'react'
import TestInfo from './Components/TestInfo'
function DisplayHome() {
  const [page, setPage] = useState(0)
  const Info = ['Test/Exams Info', 'Create Questions', 'Final'];

  const Display = () => {
    if(page === 0){
      return <TestInfo />
    }else if(page === 1){
      return <h3>questions Form</h3>
    }else {
      return <h1>Last Submit Informations</h1>
    }
  }

  return (
    <div>
      <h4>Display__Home</h4>

      <h1>{Info[page]}</h1>    
      <div>
        <Display />
      </div>

      <button disabled ={page === 0} onClick={()=>{ setPage((pages)=> pages - 1) }}>Prev</button>
      <button disabled ={page === Info.length - 1} onClick={()=>{ setPage((pages)=> pages + 1) }}>Next</button>

    </div>
  )
}

export default DisplayHome
