import axios from 'axios';
import React, { useState, useContext, useRef } from 'react'
import { Link } from 'react-router-dom';
import { DataContext } from '../../Context/DataContext';
import { toast } from 'react-toastify';


function TestRequest({ questions }) {

  const { setLoading, setMsg, navigate, handleButtonSubmit } = useContext(DataContext);
  const username = localStorage.getItem('email');
  const password = localStorage.getItem('password');

  // Test variables ...
  const [exam_number, setExam_number] = useState("");
  const [name, setName] = useState("");
  const [pass_mark, setPass_mark] = useState("");
  const [description, setDescription] = useState("");
  const [instructions, setInstructions] = useState("");
  const [start, setStart] = useState("");
  const [dataTime, setDataTime] = useState("");
  const [duration, setDuration] = useState("");
  const [timed, setTimed] = useState(true);
  const [created, setCreated] = useState(new Date().toISOString());
  const [open, setOpen] = useState(true);
  const date = new Date().toISOString().substring(0, 10);
  // const [clo] = useState()

  function Run(time) {
    var sp = time.split(':');
    var min = sp[0];
    var sec = sp[1];
    var data = Number(min) * 60 + Number(sec);
    return setDuration(data);

  }


  // Run(dataTime)
  //handle submit functions
  function handleSubmit(e) {
    e.preventDefault();
    setLoading(true);
    const data = {
      exam_number, name, pass_mark, description, instructions, start, duration: dataTime,
      timed, questions, open,
    };
    console.log(data)

    const jsonData = JSON.stringify(data); //convert the data into json format   
    const url = 'https://morning-gorge-09623.herokuapp.com/api/v1/exam'; // posting data into new api
    axios.post(url, jsonData, {
      crossDomain: true,
      // withCredentials: true,
      headers: {
        'accept': 'application/json',
        "Content-type": "application/json;charset=UTF-8",
        'Access-Control-Allow-Origin': 'http://localhost:3000/',
        'Access-Control-Allow-Methods': 'POST',
        'Access-Control-Allow-Headers': 'Content-Type',
      },
      auth: {
        username: JSON.parse(username),
        password: JSON.parse(password)

      }
    })
      .then((response) => {
        setLoading(false);
        // alert(`New Exam Added Successfully`);
        toast.success('New Exam Added Successfully');
        navigate('/dashboard/getalltest');
        console.log(response);
      })
      .catch((error) => {
        if (error.response) {

          setLoading(false)
          setMsg(error.response.data.error_message);
          console.error("ERROR>>>>", error)
          console.log("ERROR>>>>Message", error.message);
          setMsg(error.response.data.error_message)
          toast.error(error.response.data.error_message);

        } else {
          console.log(`ERROR>>>>eLSE: ${error.message}`)
          console.log('No internet............')
        }

      })

    setExam_number("");
    setDescription("");
    setName("");
    setPass_mark("");
    setStart("");
    setDuration("");
    setInstructions("");
    setTimed("");

  }
  return (
    <div className='container'>


      <div className=''>
        <h4><Link to='/dashboard'>Dashboard</Link> &gt; New Exam </h4>
      </div>

      <div className=''>
        <form onSubmit={handleSubmit}>
          <div>
            <label>
              Test Number:
              <input
                type='text'
                name='test_number'
                onChange={(e) => { setExam_number(e.target.value) }}
                value={exam_number.toUpperCase()}
              />
            </label>
          </div>
          <div>
            <label>
              Test Name:<input
                type='text'
                name='name'
                onChange={(e) => { setName(e.target.value) }}
                value={name}
              />
            </label>
          </div>
          <div>
            <label>Pass Mark: <input
              type='number'
              name='pass_mark'
              onChange={(e) => { setPass_mark(e.target.value) }}
              value={pass_mark}
            /> </label>
          </div>

          <div><label>Description: <textarea
            name='describtion'
            onChange={(e) => { setDescription(e.target.value) }}
            value={description}
          />
          </label>
          </div>

          <div><label>Instruction:
            <textarea
              name='instruction'
              onChange={(e) => { setInstructions(e.target.value) }}
              value={instructions}
            />
          </label>
          </div>
          <div className='openClose'>
            <div>
              <label htmlFor='openTrue' style={{ background: open ? 'grey' : '' }} className='open'>Open Test
                <input type='checkbox' value={open} style={{ display: 'none' }} id='openTrue' onChange={(e) => setOpen(...true)} />
              </label>
            </div>
            <div>
              <label htmlFor='openFalse' style={{ background: open ? '' : 'grey' }} className='open'>Close Test
                <input type='checkbox' value={open} style={{ display: 'none' }} id='openFalse' onChange={(e) => setOpen(...false)} />
              </label>
            </div>
          </div>
          <div>
            <label>
              Start: <input
                type='datetime-local'
                name='start'
                onChange={(e) => { setStart(e.target.value) }}
                value={start}
              />
            </label>
          </div>



          <p>
            <label htmlFor='timeTrue' style={{ background: timed ? 'grey' : 'none' }} className='time'>Timed</label>
            <input type='checkbox' value={timed} id='timeTrue' style={{ display: 'none' }} onChange={(e) => { setTimed(true) }} />
          </p>

          <p>
            <label htmlFor='timeFalse' style={{ background: !timed ? 'grey' : 'none' }} className='not_time'>Not Timed</label>
            <input type='checkbox' value={timed} id='timeFalse' style={{ display: 'none' }} onChange={(e) => { setTimed(false) }} />
          </p>


          <p style={{ display: timed ? 'block' : 'none', marginTop: '15px' }}>

            <label>Exam Duration: min(05:00) max(23:00) <input
              type="time"
              name='duration'
              min="05:00"
              max="23:00"
              formNoValidate
              onChange={(e) => {
                const time = e.target.value;
                var sp = time.split(':');
                var min = sp[0];
                var sec = sp[1];
                var data = Number(min) * 60 + Number(sec);
                setDataTime(data);
                timed ? setDuration(time) : setDuration("");
              }}
              value={duration}

            />
            </label>
          </p>

          {/* error when time is false FIX ME******/}


          <button ref={handleButtonSubmit} type='submit' style={{ display: 'none' }} disabled={questions.length === 0} >Submit</button>
        </form>

      </div>


    </div>
  )
}

export default TestRequest
