import axios from 'axios';
import React, { useState } from 'react'
import { toast } from 'react-toastify';

function CreateUser() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);
  const [role, setRole] = useState("");
  const [msg, setMsg] = useState(false);
  const [submitted, setSubmitted] = useState(false);


  const handleSubmit = (e) => {
    e.preventDefault()
    setLoading(true)
    setSubmitted(true)
    const data = { name, email, password, role }
    const jsonData = JSON.stringify(data);

    if (email) {
      if(email.includes('@')){
      if (password) {
        if (password.length >= 6 && password.length <= 16) {
          if (role && role === "TESTOWNER") {
            console.log(data)
          axios.post('https://morning-gorge-09623.herokuapp.com/api/v1/auth/register', jsonData,
              {
                headers: {
                  'accept': 'application/json',
                  "Content-type": "application/json;charset=UTF-8",
                  'Access-Control-Allow-Origin': 'http://localhost:3000/',
                  'Access-Control-Allow-Methods': 'POST',
                  'Access-Control-Allow-Headers': 'Content-Type',
                }
              })
              .then((response) => {
                if (response.status === 201) {
                  toast.success("New User Created Successfully...")
                }
                setLoading(false)
              })
              .catch((error) => {
                toast.error(error.message , { theme: "colored" }); //only if no internet connection
                setLoading(false)
                if (error.response.status === 409) {
                  toast.error("User Aleardy Exits");
                  setLoading(false)
                }
                toast.error(error.response.data.error_message);
                setLoading(false)
                setMsg(error.response.data.error_message)
              })

          } else {
            toast.error('Role can not be empty', { theme: "colored" });
            setLoading(false)
          }
        } else {
          toast.error('password must be between 6 and 16 characters', { theme: "colored" });
          setLoading(false)
        }
      }}else{
        toast.error('Invalid Email', { theme: "colored" });
            setLoading(false)
      }
    }


  }

  return (
    <>
      <h3>CreateUser TESTOWNER or Candidates</h3>

      {loading && <p style={{ background: 'black', color: '#fff' }}>Loading...</p>}
      {msg && <p style={{ background: 'red', color: '#fff' }}>{msg}</p>}

      <form onSubmit={handleSubmit}>
        <label>Name: <input type='text' value={name} onChange={(e) => setName(e.target.value)} /></label> <br /><br />
        <label>email: <input type='text' value={email} onChange={(e) => setEmail(e.target.value)} /></label><br /><br />


        <label>password: {submitted && !password ? <span className='error'>Password cannot be empty </span> : null}
        <input type='password' value={password} onChange={(e) => setPassword(e.target.value)} /></label><br /><br />

        <label>Role:
          <select
            onChange={(e) => setRole(e.target.value)}
          >

            <option>----</option>
            <option value='TESTOWNER'>Test owner</option>
            <option value='CANDIDATE'>Candidates</option>
          </select>

        </label>

        <button>Create</button>
      </form>
      <br />
      <a href='/'>Already account Login</a>


    </>
  )
}

export default CreateUser
