export const DataContext = createContext({});

function DataProvider(props){

    const [name, setName] = useState('Musa');
    const age = 23;

  const value = [name, age]

    return(
        <DataContext.Provider value={value}>
            {props.children}
        </DataContext.Provider>
    )
}

