        //     console.log("RESPONSE:::STATUS",response.status);
        // })
        // .catch((error)=>{
        //     console.log("Error:::Message", error.message)
        // })
    console.log(id)
    }

  return (
    <div className='container'>
        <h3>All Users</h3>
     {  loading && <p style={{background:'black', color:'#fff'}}>Loading...</p>}

        <table border='1px'>
        <thead>
            <tr>
                <th>Name</th>
                <th>Email</th>
                <th>Role</th>
                <th>Delete</th>
            </tr>
        </thead>
        <tbody>

        {data && data.map((user)=>{
           return<tr key={user.id}>
           <td>{user.name}</td>
           <td>{user.email}</td>
           <td>{user.role}</td>
           <td><button onClick={()=>handleDelete(user)}>Delete</button></td>
           </tr>
       })}


       </tbody>
       </table>

       <a href='/' >Login As Admin</a>


    </div>
  )
}

export default GetAllUser
