import axios from 'axios';
import React, { useState } from 'react'
import { toast } from 'react-toastify';

function CreateUser() {
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [loading, setLoading] = useState(false);
    const [role, setRole] = useState("");
    const [msg, setMsg] = useState(false);


    const handleSubmit = (e) => {
        e.preventDefault()
        setLoading(true)
        setMsg(false)
        const data = {name, email, password, role}
        const jsonData = JSON.stringify(data);

        axios.post('https://morning-gorge-09623.herokuapp.com/api/v1/auth/register', jsonData,
        {headers:{
          'Accept': 'application/json, text/plain',
          'Content-Type': 'application/json;charset=UTF-8'
        }} )
        .then((response)=>{
          console.log("RESPONSE",response);
          console.log("RESPONSE:::DATA",response.data);
          console.log("RESPONSE:::STATUS",response.status)
          if(response.status === 201){
          toast.success("New User Created Successfully...");
          }
          setLoading(false)

        })
        .catch((error)=>{
          console.log(error)
          console.log("Error Message:::",error.message)
          console.log("Error in Axios:::",error.response.data.error_message)
          if(error.response.status === 409){
          toast.error("User Aleardy Exits");

          }
          toast.error(error.response.data.error_message);

          setLoading(false)
          setMsg(error.response.data.error_message)
        })


        console.log(data)
    }

  return (
    <div className='container'>
    <h3>CreateUser TESTOWNER or Candidates</h3>

    {loading && <p style={{background:'black', color:'#fff'}}>Loading...</p>}
    {msg && <p style={{background:'red', color:'#fff'}}>{msg}</p>}

    <form onSubmit={handleSubmit}>
            <label>Name: <input type='text' value={name} onChange={(e)=>setName(e.target.value)} /></label> <br /><br />
            <label>email: <input type='email' value={email} onChange={(e)=>setEmail(e.target.value)} /></label><br /><br />
            <label>password: <input type='password' value={password} onChange={(e)=>setPassword(e.target.value)} /></label><br /><br />
            <label>Role:
            <select 
            onChange={(e)=>setRole(e.target.value)}
            >

            <option>----</option>
            <option value='TESTOWNER'>Test owner</option>
            <option value='CANDIDATE'>Candidates</option>
            </select>

            </label>

          <button>Create</button>
        </form>
        <br />
        <a href='/'>Login</a>
       <br /><br />
      <a href='/getalluser'>All User</a>

    </div>
  )
}

export default CreateUser
