import React, { useState, useEffect } from "react";
// import CtaButton from "../../Components/CtaButton";
// import "./Signup.css";
import validation from "./validation";
import { Link } from "react-router-dom";
import axios from "axios";
const url = "https://morning-gorge-09623.herokuapp.com/api/v1/auth/register";
function Signup() {
  const [values, setValues] = useState({
    fullname: "",
    email: "",
    password: "",
    confirmpass: "",
  });
  const [isSubmit, setisSubmit] = useState(false);
  const [errors, setErrors] = useState({});

  const role = "CANDIDATE";
  const hanldeChange = (e) => {
    setValues({
      ...values,
      [e.target.name]: e.target.value,
    });
  };
  const handleSubmit = async (e) => {
    e.preventDefault();
    setErrors(validation(values));

    try {
      const resp = await axios.post(url, {
        name: values.fullname,
        email: values.email,
        password: values.password,
        role: role,
      });
      console.log(resp.data);
    } catch (error) {
      console.log(error);
    }
    setisSubmit(true);
  };

  useEffect(() => {
    if (Object.keys(errors).length === 0 && isSubmit) {
      <div className="successfull">
        <div className="message">Signed in successfully</div>
      </div>;
    }
  }, [errors]);

  return (
    <>
      <div className="container-mobile">
        <nav>
          <img className="background-img" src="surr-829 1.png" />
          <img className="logo" alt="" src="images/FSAF CBT-1.png" />
        </nav>

        <div className="container-form">
          <h1 className="title">Sign Up</h1>

          <form>
            <div className="input-field">
              <label for="email"> email</label>
              <input
                type="email"
                placeholder="kanayokanayo@gmail.com"
                name="email"
                value={values.email}
                onChange={hanldeChange}
              />
              {errors.email && <p className="error">{errors.email}</p>}
            </div>
            <div className="input-field">
              <label for="name">Full name</label>
              <input
                type="text"
                name="fullname"
                placeholder="Daniel"
                value={values.fullname}
                onChange={hanldeChange}
              />
              {errors.fullname && <p className="error">{errors.fullname}</p>}
            </div>

            <div className="input-field">
              <label for="password">Password</label>
              <input
                type="password"
                placeholder="*********"
                name="password"
                value={values.password}
                onChange={hanldeChange}
              />
              {errors.password && <p className="error">{errors.password}</p>}
            </div>

            <div className="submit">
              <button
                type="button"
                buttonStyle="btn--secondary"
                buttonSize="btn--large"
                children="Register"
                onClick={handleSubmit}
              >Register </button>
            </div>
          </form>
          <div className="signup">
            <Link to="/login" className="linktoLogin">
              Already have an account?
            </Link>
          </div>
        </div>
      </div>

      <div className="container-desktop">
        <nav>
          <img className="logo" alt="" src="images/FSAF CBT-1.png" />
        </nav>
        <div className="login-form">
          <img
            className="background-img-desktop"
            alt=""
            src="images/surr-829 1.png"
          />

          <div className="container-form">
            <div className="signup-header">
              <h1 className="title">Sign Up</h1>
            </div>

            <form>
              <div className="input-field">
                <label for="email"> email</label>
                <input
                  type="email"
                  placeholder="kanayokanayo@gmail.com"
                  name="email"
                  value={values.email}
                  onChange={hanldeChange}
                />
                {errors.email && <p className="error">{errors.email}</p>}
              </div>
              <div className="input-field">
                <label for="name">Full name</label>
                <input
                  type="text"
                  name="fullname"
                  placeholder="Daniel"
                  value={values.fullname}
                  onChange={hanldeChange}
                />
                {errors.fullname && <p className="error">{errors.fullname}</p>}
              </div>

              <div className="input-field">
                <label for="password">Password</label>
                <input
                  type="password"
                  placeholder="*********"
                  name="password"
                  value={values.password}
                  onChange={hanldeChange}
                />
                {errors.password && <p className="error">{errors.password}</p>}
              </div>

              <div className="submit">
                <button
                  type="button"
                  buttonStyle="btn--secondary"
                  buttonSize="btn--large"
                  children="Register"
                  onClick={handleSubmit}
                >Register </button>
              </div>
            </form>
            <div className="signup">
              <Link to="/login" className="linktoLogin">
                {" "}
                Already have an account?
              </Link>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default Signup;
