import axios from 'axios';
import React from 'react'
import useFetch from './Components/CustomHooks/useFetch'

function GetAllUser() {
    const {data, msg, loading} = useFetch('https://morning-gorge-09623.herokuapp.com/api/v1/auth/');

    function handleDelete({id}){
        axios.delete('https://morning-gorge-09623.herokuapp.com/api/v1/auth/'+ id)
        .then((response)=>{
            console.log("RESPONSE",response);
            console.log("RESPONSE:::DATA",response.data);
            console.log("RESPONSE:::STATUS",response.status);
        })
        .catch((error)=>{
            console.log("Error:::Message", error.message)
        })
    console.log(id)
    }

  return (
    <div className='container'>
        <h3>All Users</h3>
     {  loading && <p style={{background:'black', color:'#fff'}}>Loading...</p>}
         {msg && <p style={{background:'red', color:'#fff'}}>{msg}</p>}

        <table border='1px'>
        <thead>
            <tr>
                <th>Name</th>
                <th>Email</th>
                <th>Role</th>
                <th>Delete</th>
            </tr>
        </thead>
        <tbody>

        {data.map((user)=>{
           return<tr key={user.id}>
           <td>{user.name}</td>
           <td>{user.email}</td>
           <td>{user.role}</td>
           <td><button onClick={()=>handleDelete(user)}>Delete</button></td>
           </tr>
       })}


       </tbody>
       </table>

       <a href='/adminlogin' >Login As Admin</a>
    </div>
  )
}

export default GetAllUser
