import React, {useContext, useState}  from 'react'
import { DataContext } from '../Context/DataContext';


function TestInfo({record, setRecord, setDataTime}) {

  //handle submit functions
  const {handleButtonSubmit} = useContext(DataContext)

  return(
     <div>

     <div className='container'>

        <div className='head_schedule'> 
                  <h5>Created:  <em> {new Date().getFullYear()} </em></h5>
        </div>

      <div className='test_request'>
      <form>  
          <label>Test Number: <input type='text' value={record.test_number} onChange = {(event) => 
              setRecord({...record, test_number: event.target.value}) } />
          </label>


          <label> Test Name: <input type='text' value={record.name} onChange = {(event) => 
            setRecord({...record, name: event.target.value})} />
          </label>

          <label>Pass Mark: <input type='number' name='pass_mark' value={record.pass_mark} onChange = {(event) => 
            setRecord({...record, pass_mark: event.target.value})}/> 
          </label>

          <label>Description: <textarea name='describtion' value={record.description} onChange = {(event) => 
            setRecord({...record, description: event.target.value})}/>
          </label>

          <label>Instruction: <textarea name='instructions' value={record.instructions} onChange = {(event) => 
              setRecord({...record, instructions: event.target.value})}/>
          </label>

          <label> Start: <input type='datetime-local' name='start' value={record.start}
              onChange = {(event) => setRecord({...record, start: event.target.value})} />
          </label> 


          <p>
            <label htmlFor='timeTrue' style={{background: record.timed ? 'grey': 'none'}} className='time'>Timed</label>
            <input type='checkbox' id='timeTrue'  style={{display:'none'}}  onChange = {(event) => setRecord({...record, timed: true})} /> 
          </p>

          <p>
            <label htmlFor='timeFalse' style={{background: !record.timed ? 'grey': 'none'}} className='not_time'>Not Timed</label>
            <input type='checkbox' id='timeFalse'  style={{display:'none'}} onChange = {(event) => setRecord({...record, timed: false})} /> 
          </p>

          <p style={{display: record.timed ? 'block' : 'none', marginTop:'15px'}}>

          <label>Exam Duration: <input type="time" name='duration'  onChange={(e)=>{
              const time = e.target.value;
              var sp = time.split(':');
              var min = sp[0];
              var sec = sp[1];
              var data = Number(min) * 60 + Number(sec);
              setDataTime(data)
              setRecord({...record, duration: e.target.value})
            }}  
            value={record.duration}

            />
          </label>
          </p>
          <button ref={handleButtonSubmit} >Submit</button>
    </form>
        </div>
     </div>

     </div>
     ); 

}
