import { useContext, useState } from 'react';
import TestRequest from './TestRequest';
import QuestionList from './QuestionList';
import { DataContext } from '../../Context/DataContext';
import { FaArrowLeft, FaArrowRight } from 'react-icons/fa';
import {v4 as uuid} from 'uuid';

function FormQuestion() {
  const {style, loading, msg, setStyle, handleButtonSubmit} = useContext(DataContext);
 const Unique_id = uuid();
 const small = Unique_id.slice(0,8);
//   Questions Variables.......
  const [point, setPoint] = useState("");
  const [text, setText] = useState(); //quetion var txt 
  const [answer, setAnswer] = useState(); //radio button tragger and set the answer
  const [optA, setOptA] = useState("");
  const [optB, setOptB] = useState("");
  const [optC, setOptC] = useState("");
  const [optD, setOptD] = useState("");



  const [questions, setQuestions] = useState([]); //Array of question


  function handleQuestionSubmit(e){
  e.preventDefault();
     const options = [optA, optB, optC, optD]; // option ie answers 
     const id = Math.random();
     const datas = {text, point, options, answer, id}; // object to store descriptions & array of options 
     const listOfQuestions = [datas]; //Array that store question,answer,options,point

     setQuestions([...questions, {
      id:id,
      text: datas.text,
      answer: datas.answer,
      point: datas.point,
      options: datas.options
    }])  

    questions.push(listOfQuestions);

    //  console.log(listOfQuestions)
    //  console.log(questions.length);
    // try ans set all the question to new state and pass the state to parent form
    // console.log(questions)

     //   Question Variables....
     e.preventDefault();
     setText('');
     setPoint('');
     setAnswer(false);
     setOptA('');
     setOptB('');
     setOptC('');
     setOptD('');
  }


  return (
    <div className="form_body">

  <div className='formSchedule'>

      <div className='test_desc' style={{display: style ? 'none': ''}} >
            <TestRequest questions={questions}   />
      </div> 

      <div className='form_question' style={{display: !style ? 'none': ''}}  >
      {loading && <p>Loading...</p>}
      {msg && <p  style={{display:'block', color:'#fff', background: 'red'}}>{msg}</p>}
        <h3>Q{questions.length === 0 ? '': `${questions.length}`}</h3>
            <form onSubmit={handleQuestionSubmit}>
            <h4>Red</h4>
            <div>
              <label>Question <textarea name='question'  value={text} onChange={(e)=> setText(e.target.value) } /> </label><br />
              <label>Points <input type='number' name='point' value={point} onChange={(e)=> setPoint( e.target.value)} /> </label> <br />
            <h3>Options</h3>
            <div>
           <label><input placeholder='A' type='text' name='A' value={optA} onChange={(e)=> setOptA( e.target.value)} />
            <input
                type='radio' 
                name='answer'
                value={optA} 
                onChange={(e)=> setAnswer(e.target.value)} 
                checked={answer === optA}
              />
            </label> 
           </div> 

           <div>
           <label><input placeholder='B' type='text' name='B' value={optB} onChange={(e)=> setOptB( e.target.value)} /> 
           <input
               type='radio' 
               name='answer' 
               value={optB}
               onChange={(e)=> setAnswer(e.target.value)}
               checked={answer === optB}  
               /> 
            </label>
           </div>

           <div>
           <label><input placeholder='C' type='text' name='C' value={optC} onChange={(e)=> setOptC( e.target.value)} /> 
           <input
               type='radio' 
               name='answer' 
               value={optC} 
               onChange={(e)=> setAnswer(e.target.value)}  
               checked={answer === optC}
               />
            </label>  
           </div>

           <div>
           <label><input placeholder='D' type='text' name='D' value={optD} onChange={(e)=> setOptD( e.target.value)} /> 
          <input
           type='radio' 
           name='answer' 
           value={optD} 
           onChange={(e)=> setAnswer(e.target.value)}  
           checked={answer === optD}
           /> </label>    
           </div>

            <button>Add</button>

            </div>

            </form>

              <button  disabled ={questions.length === 0} onClick={() => { handleButtonSubmit.current.click()}}>Final Submit</button>

      </div>
  </div>

    <button onClick={()=>setStyle(!style)} className='proceedBtn'> {style ? 'Prev': 'Next'} {style ? <FaArrowLeft /> : <FaArrowRight /> }  </button>

      <div className='question_list' style={{display: style ? 'block': 'none'}}>
        <QuestionList questions={questions} setQuestions={setQuestions}   />     
      </div> 

    </div>


  );
}

export default FormQuestion;
