import React, { useState } from 'react'

function TestInfo({record, setRecord}) {


      // Test variables ...
const [test_number, setTest_number] = useState("");
const [name, setName] = useState("");
const [pass_mark, setPass_mark] = useState("");
const [description, setDescription] = useState("");
const [instructions, setInstructions] = useState("");
const [start, setStart] = useState("");
const [duration, setDuration] = useState("");
const [timed, setTimed] = useState(false)
const [created, setCreated] = useState(new Date().toISOString());
const [questions, setQuestions] = useState([])
const [loading, setLoading] = useState(false);


const date = new Date().toISOString().substring(0, 10);
const [msg, setMsg] = useState('');

//handle submit functions




  return(
     <div>

     <div className='head_schedule'>

              <h5>Created:  <em> {date} </em></h5>
     </div>

   <div className='schedule_form'>


         <p>
           <label>
             Test Number: <br /><input 
                 type='text' 
                 name='test_number' 
                 onChange={(e) => {setTest_number(e.target.value)} }
                 value={test_number}
                 />          
           </label>
         </p>
         <p>
         <label>
         Test Name: <br /><input 
               type='text' 
               name='name' 
               onChange={(e) => {setName(e.target.value)} }
               value={name}
               />
         </label>
         </p>
         <p>
         <label>Pass Mark: <br /><input 
               type='number' 
               name='pass_mark' 
               onChange={(e) => {setPass_mark(e.target.value)}}
               value={pass_mark}
               /> </label>
         </p>      

           <p><label>Description: <br /><textarea 
               name='describtion' 
               onChange={(e) => {setDescription(e.target.value)} }
               value={description}
               />
               </label>
           </p>

           <p><label>Instruction: <br />
           <textarea 
               name='instruction' 
               onChange={(e) => {setInstructions(e.target.value)} }
               value={instructions}
           />
           </label>
           </p>

           <p>
           <label>
               Start: <br /><input 
                   type='datetime-local' 
                   name='start'  
                   onChange={(e)=>{setStart(e.target.value)}}  
                   value={start}
                 />
           </label> 
           </p>

           <p><label>Dur in sec: <br /><input 
               type="number" 
               name='dur' 
               step='1' 
               min="02:00:00" 
               max="20:00:00" 
               onChange={(e)=>{setDuration(e.target.value)}}  
               value={duration}
               />
           </label>
           </p>
           <p>
             <label>Timed: <input 
             type='checkbox' 
             name='timed' 
             checked={timed}
             onChange={(e)=> {setTimed(!timed) }} />
             </label>
           </p>  


   </div>
     </div>
     ); 

}

export default TestInfo
