import axios from 'axios';
import {useEffect, useState} from 'react'
import { toast } from 'react-toastify';
import useFetch from '../CustomHooks/useFetch'

function GetAllUser() {

    const url = 'https://morning-gorge-09623.herokuapp.com/api/v1/auth/';
    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [msg, setMsg] = useState("")

    useEffect(()=>{  
        axios.get(url)
        .then((response)=>{
            if(response.status === 200){
                toast.success('Fetch Record Successfully', { theme: "colored" }); //only if no internet connection
                setData(response.data);
                setMsg(null);
                setLoading(false);
            }

        })
        .catch((error)=>{
            console.log(error.message);
            setMsg(error.message);
            toast.error(error.message, { theme: "colored" }); //only if no internet connection
            setLoading(false);      
            setData(null)
        })

    },[])


    function handleDelete({id}){
        // axios.delete('https://morning-gorge-09623.herokuapp.com/api/v1/auth/'+ id)
        // .then((response)=>{
        //     console.log("RESPONSE",response);
        //     console.log("RESPONSE:::DATA",response.data);
        //     console.log("RESPONSE:::STATUS",response.status);
        // })
        // .catch((error)=>{
        //     console.log("Error:::Message", error.message)
        // })
    console.log(id)
    }

  return (
    <div className='container'>
        <p>All Users    {  loading && <span>Loading...</span>}</p>


        <table border='1px'>
        <thead>
            <tr>
                <th>Name</th>
                <th>Email</th>
                <th>Role</th>
                <th>Delete</th>
            </tr>
        </thead>
        <tbody>

        {data && data.map((user)=>{
           return<tr key={user.id}>
           <td>{user.name}</td>
           <td>{user.email}</td>
           <td>{user.role}</td>
           <td><button onClick={()=>handleDelete(user)}>Delete</button></td>
           </tr>
       })}


       </tbody>
       </table>

       <a href='/' >Login As Admin</a>


    </div>
  )
}

export default GetAllUser
