import axios from 'axios';
import React, { useContext, useState } from 'react'
import { Link } from 'react-router-dom';
import { toast } from 'react-toastify';
import { DataContext } from '../../Context/DataContext';
import { Container, Input, LoginForm } from '../../Styled';


function AdminLogin() {

  const { navigate } = useContext(DataContext);

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);
  const [msg, setMsg] = useState(false);
  const [name, setName] = useState("");
  const [submitForm, setSubmitForm] = useState()
  const [valid, setValid] = useState(false);
  const [errorMsg, setErrorMsg] = useState({
    nameError: "",
  })

  const handleSubmit = (e) => {
    e.preventDefault();
    const data = { email, password, name }
    const jsonData = JSON.stringify(data)
    setSubmitForm(true); 

    if(name && password && email){
     alert('submiteed')
    }else if(submitForm && !name){
      setErrorMsg.nameError('Name Field INput is needed')
    }




  }

  return (
    <Container>
      <LoginForm>
        <div>
          <h3>Admin Login. </h3>
          {loading && <p>Loading...</p>}
          {msg && <p>{msg}</p>}
        </div>

        <div>
          <form onSubmit={handleSubmit}>
            <label>Name {errorMsg.nameError}
              <Input type='text' value={name} onChange={(e) => setName(e.target.value)} />
            </label>

            <label>Email  
            {submitForm && !email  ? submitForm && !email.includes('@') ?
              <span style={{color:'red'}}>Email Filed Can Not Blank</span> :
              <span style={{color:'red'}}>Email is not valid</span>:
              null
            }



            <Input type='text' value={email} onChange={(e) => setEmail(e.target.value)} /></label>

            <label>Password  {submitForm && !password ? <span style={{color:'red'}}>Password Filed Can Not Blank</span>: null}
             <Input type='password' value={password} onChange={(e) => setPassword(e.target.value)} /></label>
            <button>Submit</button>
          </form>
        </div>
      </LoginForm>
      <Link to='/admin'> Login</Link>

    </Container>
  )
}

export default AdminLogin
