import axios from 'axios';
import React, { useContext, useEffect, useState } from 'react'
import { Link } from 'react-router-dom';
import { toast } from 'react-toastify';
import { DataContext } from '../../Context/DataContext';
import { Container, Input, LoginForm } from '../../Styled';
const url = "https://morning-gorge-09623.herokuapp.com/api/v1/auth/";

function AdminLogin() {

  const { navigate } = useContext(DataContext);

  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);
  const [role, setRole] = useState("");
  const [msg, setMsg] = useState(false);






  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = {name, email, password};
    const jsonData = JSON.stringify(data);

     axios.post('https://morning-gorge-09623.herokuapp.com/api/v1/auth/register', jsonData,
    {headers:{
      'Accept': 'application/json, text/plain',
      'Content-Type': 'application/json;charset=UTF-8'
    }} )
    .then((response)=>{
      console.log("RESPONSE",response);
      console.log("RESPONSE:::DATA",response.data);
      console.log("RESPONSE:::STATUS",response.status);
      if(response.status === '201'){
         toast.success("Login Successfully", { theme: "colored" });
          navigate('/admin');

      }
      setLoading(false)
      setMsg(response.status)
    })
    .catch((error)=>{
      console.log(error)
      console.log("Error Message:::",error.message)
      console.log("Error in Axios:::",error.response.data.error_message)
      setLoading(false)
      setMsg(error.response.data.error_message)
    })
    console.log(data)
    localStorage.setItem('name', data.name);
    localStorage.setItem('email', data.email);
    localStorage.setItem('password', data.password);

  }



  return (
    <Container>
      <LoginForm>
        <div>
          <h3>Admin Login. </h3>

        </div>

        <div>
          <form onSubmit={handleSubmit}>
            <label>Name  
              <Input type='text' value={name} onChange={(e) => setName(e.target.value)} />
            </label>

            <label>Email 
              <Input type='text' value={email} onChange={(e) => setEmail(e.target.value)} /></label>
            <label>Password  
              <Input type='password' value={password} onChange={(e) => setPassword(e.target.value)} /></label>
            <button>Submit</button>
          </form>
        </div>
      </LoginForm>
      <Link to='/admin'> Login</Link>


    </Container>
  )
}

export default AdminLogin
