import axios from 'axios';
import React, { useContext, useState } from 'react'
import { Link } from 'react-router-dom';
import { toast } from 'react-toastify';
import { DataContext } from '../../Context/DataContext';
import { Container, Input, LoginForm } from '../../Styled';

function AdminLogin() {

  const { navigate } = useContext(DataContext);

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);
  const [role, setRole] = useState("");
  const [msg, setMsg] = useState(false);

  const [submitted, setSubmitted] = useState(false);
  const [errors, setErrors] = useState({});
  const [valid, setValid] = useState(false);


  const Validate = (values) => {
    const errors = {};
    if (!values.email) {
      errors.email = "email is required";
    } 

    return errors
  }

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = { email, password };
    const jsonData = JSON.stringify(data);
    // setLoading(true)

    setErrors(Validate(data));
    setSubmitted(true);

    if(email){
      setValid(true);
        console.log(data)
        setLoading(false)
    }

    /*
    axios.post('https://morning-gorge-09623.herokuapp.com/api/v1/auth', jsonData,
      {
        headers: {
          'accept': 'application/json',
          "Content-type": "application/json;charset=UTF-8",
          'Access-Control-Allow-Origin': 'http://localhost:3000/',
          'Access-Control-Allow-Methods': 'POST',
          'Access-Control-Allow-Headers': 'Content-Type',
        }
      }) 
      .then((response) => {
        if (response.status === 200 ) {
          toast.success("Login Successfully...");
          localStorage.setItem('email', data.email);
          localStorage.setItem('password', data.password);
          console.log("RESPONSE", response);
          console.log("RESPONSE:::DATA", response.data);
          console.log("RESPONSE:::STATUS", response.status)
          navigate('/admin')
        }
        setLoading(false)

      }).catch((error) => {
        console.log(error)
        console.log("Error Message:::", error.message)
        console.log("Error in Axios:::", error.response.data.error_message)
        setLoading(false)
        toast.error(error.response.data.error_message, { theme: "colored" });
        setMsg(error.response.data.error_message)
      })
      */


  }



  return (
    <Container>
      <LoginForm>
        <div>
          <h3>Admin Login. </h3>
          {loading && <p>Loading....</p>}
        </div>

        <div>
          <form onSubmit={handleSubmit}>
            <label>Email  { errors.email && <p className="error">{errors.email}</p> }
              <Input type='text' value={email} onChange={(e) => setEmail(e.target.value)} /></label>
            <label>Password {submitted && !password ? <span className='error'>Password cannot be empty </span> : null }
              <Input type='password' value={password} onChange={(e) => setPassword(e.target.value)} /></label>
            <button>Submit</button>
          </form>
        </div>
      </LoginForm>
      <Link to='/createuser'> Create new account</Link>


    </Container>
  )
}

export default AdminLogin
