import axios from 'axios'
import React, { useState } from 'react'

function GPD() {

    // GET Forms Variables........
    const [id, setId] = useState('');
    const [data, setData] = useState([]);
    const [msg, setMsg] = useState('');
    const [loading, setLoading] = useState(false);


// Upadate variables....    
const [text, setText] = useState(""); //quetion var txt 

    function handleSearch(e){
    e.preventDefault();
    setLoading(true)
   fetch(`https://enigmatic-cove-89902.herokuapp.com/api/v1/test/${id}`)
    .then((res)=>{
        if(res.ok){
            console.log('SUCCESS')     
            setMsg()
            setLoading(false);
            setId('')
            return res.json()
        }else{
            console.log('NOT SUCCESS');
            setMsg(`No record Found of ${id}`)
            setLoading(false)
        }

    }).then((result)=>{
        console.log(result);
        setData(result);
        console.log(result.name)
        setText(result.name)

    })
    .catch((error)=>{
        console.log(error);
        setMsg(error.message);
    })

 }


  return (
    <div>
        <h2>Search by Test Number Only, CS </h2>
        <h4 style={{background:'red', color:'#fff'}}>
        {loading ? "Loading...": msg}

        </h4>
        <form onSubmit={handleSearch}>
            <input 
                type='text' 
                onChange={(e)=>setId(e.target.value)} 
                value={id}
             />
             <button>Search</button>
        </form>



        <p> Name:  {data && data.name} </p>
        <p> Description:  {data && data.description} </p>
        <p> Instructions:  {data && data.instructions} </p>
        <p>Start: {data && data.start}</p>




        <form>
        <h3>Update the record</h3>
        <label>
        Course Name: <input type='text' value={text} onChange={(e)=>setText(e.target.value)} />

        </label>
        <button>Update</button>
        </form>
    </div>
  )
}

export default GPD
