import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { FaArrowLeft } from 'react-icons/fa';
import QuestionList from './QuestionList';
import {Link, useNavigate} from 'react-router-dom'
import useFetch from '../CustomHooks/useFetch';


function Swagger() {
    const [btnDelete, setBtnDelete] = useState(false);
    const navigate = useNavigate();

    const [msg, setMsg] = useState("");
    const [data, setData]  = useState([]);
    const [loading, setLoading] = useState(false);

    useEffect(()=>{
        setLoading(true)
        axios.get('https://morning-gorge-09623.herokuapp.com/api/v1/auth/register',
    {headers:{
      'Accept': 'application/json, text/plain',
      'Content-Type': 'application/json;charset=UTF-8'
    }} )
    .then((response)=>{
      console.log("RESPONSE",response);
      console.log("RESPONSE:::DATA",response.data);
      console.log("RESPONSE:::STATUS",response.status);

      if(response.status === '201'){
        setMsg("New User Create Successfully.")  
      }
      setLoading(false)
      setMsg(response.status)
    })
    .catch((error)=>{
      console.log(error)
      console.log("Error Message:::",error.message)
      console.log("Error in Axios:::",error.response.data.error_message)
      setLoading(false)
      setMsg(error.message)
    //   setMsg(error.response.data.error);

    //   setMsg(error.response.data.error_message)
    })
    })




    function convertDatetoStr(info){
        var date = info;
        var val1 = Date.parse(date);
        var val2 = new Date(val1);
        var str = val2.toLocaleString('en-US');
        return str;
    }

    function handleDelete({test_number}){


    axios.delete(`https://enigmatic-cove-89902.herokuapp.com/api/v1/test/${test_number}`)
        .then(res => {
            console.log(res);
            alert(`${test_number} Deleted Successfully`)
            window.location.reload(true);
        })
        .catch(err => console.log(err));  
    }

  return (<div className='container'>

             <div className='scheduledCont'>
             <div className='head'>
                <Link to={-1}> <FaArrowLeft /> </Link>
            </div>


             <div>
             {loading && <div>Loading...</div>}
             {msg && <div>{msg}</div>}

                {
                    data.map((item)=>{  
                        return<div className='scheduledList' key={item.id}>

                            <Link to={`/Test/${item.test_number}`} >
                                <div className='scheduled' onMouseEnter={() => { console.log("Event:MouseEnter"); setBtnDelete(true); }} >
                                    <div className='courseTitle'>{item.test_number}</div>
                                    <div><strong>{convertDatetoStr(item.created.toString()) }</strong></div>
                                </div>
                            </Link>

                                <div  style={{display: btnDelete ? 'block' : 'none'}}>
                                    <button onClick={()=>{handleDelete(item)}}>Delete exam</button>
                                </div>
                            </div>

                 }) }


             </div>    
        </div>


    </div>
  )
}
/*
  <tr>
                    <th>Test/No</th>
                    <th>Name</th>
                    <th>Instructions</th>
                    <th>Description</th>
                    <th>Duration</th>   
                    <th>Pass_mark</th>    
                    <th>Start</th>
                    <th>Created</th>
                    <th>Questions</th>
                    <th>Timed</th>
                </tr>
*/

export default Swagger
