                {submitted && !password ? <div> <span className='error'>Password cannot be empty</span>  <ErrorIcon />  </div> : null}
              </DivError>
              <Input type='password' style={{borderColor: submitted && !email ? 'red':''}} value={password} onChange={(e) => setPassword(e.target.value)} />

            </label>
            <Button>Submit</Button>
