import axios from 'axios';
import React, { useContext, useEffect, useState } from 'react'
import { Link } from 'react-router-dom';
import { toast } from 'react-toastify';
import { DataContext } from '../../Context/DataContext';
import { Container, Input, LoginForm } from '../../Styled';
const url = "https://morning-gorge-09623.herokuapp.com/api/v1/auth/register";


function AdminLogin() {

  const { navigate } = useContext(DataContext);

  const [values, setValues] = useState({
    name: "",
    email: "",
    password: "",
  });

  const [isSubmit, setisSubmit] = useState(false);
  const [errors, setErrors] = useState({});


  const validation = (values) => {
    const errors = {};
    const regex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/i;
    if (!values.name) {
      errors.name = "Name is required";
    }
    if (!values.email) {
      errors.email = "Email is required";
    } else if (!regex.test(values.email)) {
      errors.email = "this is not a valid email";
    }

    if (!values.password) {
      errors.password = "Password is required";
    } else if (values.password.length < 4) {
      errors.password = "Password must be more than 4 characters";
    } else if (values.password.length > 10) {
      errors.password = "Password cannot exceed more than 10 characters";
    }
    return errors;
  };



  const handleSubmit = async (e) => {
    e.preventDefault();
    setErrors(validation(values));
    console.log(values)
    // try {
    //    const resp =  await axios.post(url, {
    //     name: values.fullname,
    //     email: values.email,
    //     password: values.password,

    //   }) ;
    //   console.log(resp.data);
    // } catch (error) {
    //   console.log(error);
    // }
setisSubmit(true)

  }

  useEffect(() => {
    if (Object.keys(errors).length === 0 && isSubmit) {
      <div className="successfull">
        <div className="message">Signed in successfully</div>
      </div>;
    }
  }, [errors]);

  return (
    <Container>
      <LoginForm>
        <div>
          <h3>Admin Login. </h3>

        </div>

        <div>
          <form onSubmit={handleSubmit}>
            <label>Name  {errors.email && <span className='error'>{errors.name}</span>}
              <Input type='text' value={values.name} onChange={(e) => setValues({...values, name:e.target.value})} />
            </label>

            <label>Email  {errors.email && <span className='error'>{errors.email}</span>}
              <Input type='text' value={values.email} onChange={(e) => setValues(e.target.value)} /></label>
            <label>Password  {errors.email && <span className='error'>{errors.password}</span>}
              <Input type='password' value={values.password} onChange={(e) => setValues(e.target.value)} /></label>
            <button>Submit</button>
          </form>
        </div>
      </LoginForm>
      <Link to='/admin'> Login</Link>

      <div>
        <p>Name: {values.name}</p>
        <p>Email: {values.email}</p>
        <p>Password: {values.password}</p>

      </div>
    </Container>
  )
}

export default AdminLogin
