import axios from 'axios';
import React, { useState } from 'react'
import { Link } from 'react-router-dom';

function GetOneExam() {

    const [examnumber, setExamnumber] = useState("");
    const [loading, setLoading] = useState(false);
    const [msg, setMsg] = useState("");
    const  [data, setData] = useState([]);

    function convertDatetoStr(info){
        var date = info;
        var val1 = Date.parse(date);
        var val2 = new Date(val1);
        var str = val2.toLocaleString('en-US');
        return str;
    }

    function handleFind(e){
       e.preventDefault()
        console.log(examnumber);

        setLoading(true);

        axios.get(`https://morning-gorge-09623.herokuapp.com/api/v1/exam/${examnumber}`, {
            crossDomain: true,
            // withCredentials: true,
            headers: {
              'accept': 'application/json',
              "Content-type": "application/json;charset=UTF-8",
              'Access-Control-Allow-Origin': 'http://localhost:3000/',
              'Access-Control-Allow-Headers': 'Content-Type',
          },
          auth: {
            username: 'mujtaba@gmail.com',
            password: '123456'
          }
        })
            .then(res => {
                setLoading(false);
                console.log("----RESPONSE----",res);
                console.log("----RESPONSE--DATA--",res.data);
                setData(res.data);

            })
            .catch((error)=>{
                setLoading(false)
                setMsg(error.message)
                setMsg(error.response.data.error_message);

                console.error(error)
                console.log(error.message);

            });  

    }



  return (
    <div className='container'>
        <h3>Get Single Exam by Unique ID</h3>

        {loading && <div>Loading...</div>}
        { <div style={{display:'block', color:'#fff', background: 'red'}}>{ msg}</div>}


        <form onSubmit={handleFind}>
            <label>Exam Number:
                <input type='text'
                    placeholder='Exam Number' 
                    value={examnumber}
                    onChange={(e)=>setExamnumber(e.target.value.toUpperCase())}
                 /></label>
            <button>Find</button>   
        </form>


        <div>
            <table>
                <thead>
                    <tr>
                        <th>Exam</th>
                        <th>Info</th>
                    </tr>
                </thead>

                <tbody>
                    <tr>
                        <td>Name</td>
                        <td>{data.name}</td>
                    </tr>
                    <tr>
                        <td>Exam Number</td>
                        <td>{data.exam_number}</td>
                    </tr> 
                    <tr>
                        <td>Description</td>
                        <td>{data.description}</td>
                    </tr>
                    <tr>
                        <td>Instruction</td>
                        <td>{data.instructions}</td>
                    </tr>
                    <tr>
                        <td>Open</td>
                        <td>{data.open}</td>
                    </tr>
                    <tr>
                        <td>Pass_mark</td>
                        <td>{data.pass_mark}</td>
                    </tr>
                    <tr>
                        <td>Timed</td>
                        <td>{data.timed}</td>
                    </tr>
                    <tr>
                        <td>Number of Candidates</td>
                        <td>{data.candidates}</td>
                     </tr>
                     <tr>
                        <td>Registered Candidates</td>
                        <td>{data.registered_candidates}</td>
                     </tr>
                </tbody>
            </table>


        </div>



</div>
  )
}

export default GetOneExam
