import axios from 'axios';
import React, { useContext, useState } from 'react'
import { Link } from 'react-router-dom';
import { toast } from 'react-toastify';
import { DataContext } from '../../Context/DataContext';
import { Container, Input, LoginForm } from '../../Styled';

function AdminLogin() {

  const { navigate } = useContext(DataContext);

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);

  const [submitted, setSubmitted] = useState(false);


  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitted(true);
    const data = { email, password };
    const jsonData = JSON.stringify(data);
    setLoading(true)

    if (email) {
      if(email.includes('@')){
        if (password) {
          if (password.length >= 6 && password.length <= 16) {
            console.log("There is DATA", data)
            axios.post('https://morning-gorge-09623.herokuapp.com/api/v1/auth', jsonData,
              {
                headers: {
                  'accept': 'application/json',
                  "Content-type": "application/json;charset=UTF-8",
                  'Access-Control-Allow-Origin': 'http://localhost:3000/',
                  'Access-Control-Allow-Methods': 'POST',
                  'Access-Control-Allow-Headers': 'Content-Type',
                }
              })
              .then((response) => {
                if (response.status === 200) {
                  toast.success("Login Successfully...");
                  localStorage.setItem('email', data.email);
                  localStorage.setItem('password', data.password);
                  console.log("RESPONSE", response);
                  console.log("RESPONSE:::DATA", response.data);
                  console.log("RESPONSE:::STATUS", response.status)
                  navigate('/admin')
                }
                setLoading(false)
              }).catch((error) => {
                console.log(error)
                toast.error(error.message, { theme: "colored" }); //only if no internet connection
                setLoading(false)
                toast.error(error.response.data.error_message, { theme: "colored" });
              })
          } else {
            toast.error('password must be between 6 and 16 characters', { theme: "colored" });
            setLoading(false)
          }
        } else {
          setLoading(false)
        }
      }else{
        toast.error('Invalid Email Address', { theme: "colored" });
        setLoading(false)
      }

    } else {
      setLoading(false)
    }


  }



  return (
    <Container>
      <LoginForm>
        <div>
          <h3>Admin Login. </h3>
          {loading && <p>Loading....</p>}
        </div>

        <div>
          <form onSubmit={handleSubmit}>
            <label>
              Email

              {submitted && !email ? <span className='error'>Email cannot be empty </span> :
                submitted && !email.includes('@') ? <span className='error'>Invalid Email</span> :
                  null}


              <Input type='text' value={email} onChange={(e) => setEmail(e.target.value)} />
            </label>


            <label>Password {submitted && !password ? <span className='error'>Password cannot be empty </span> : null}
              <Input type='password' value={password} onChange={(e) => setPassword(e.target.value)} /></label>
            <button>Submit</button>
          </form>
        </div>
      </LoginForm>
      <Link to='/createuser'> Create new account</Link>


    </Container>
  )
}

export default AdminLogin
