import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { FaArrowLeft } from 'react-icons/fa';
import { Link, useNavigate } from 'react-router-dom'
import { toast } from 'react-toastify';


function Swagger() {
    const [btnDelete, setBtnDelete] = useState(false);
    const navigate = useNavigate();

    const [msg, setMsg] = useState("");
    const [data, setData] = useState(['FIP', 'TOTAL']);
    const [loading, setLoading] = useState(false);


    const username = localStorage.getItem('email');
    const password = localStorage.getItem('password');

   /*   useEffect(() => {
        setLoading(true);

      axios.get('https://morning-gorge-09623.herokuapp.com/api/v1/exam', {
            crossDomain: true,
            // withCredentials: true,
            headers: {
                'accept': 'application/json',
                "Content-type": "application/json;charset=UTF-8",
                'Access-Control-Allow-Origin': 'http://localhost:3000/',
                'Access-Control-Allow-Methods': 'POST',
                'Access-Control-Allow-Headers': 'Content-Type',
            },
            auth: {
                username: JSON.parse(username),
                password: JSON.parse(password)
            }
        })
            .then((response) => {
                setData(response.data);
                setLoading(false);
                setMsg("")
                console.log(data)
                console.log("Response DATA:::", response.data)

            })
            .catch((error) => {
                setLoading(false)
                // setMsg(error.message);
                toast.error(error.message, { theme: "colored" });
                console.error(error)
            })
    }, []) */


    function convertDatetoStr(info) {
        var date = info;
        var val1 = Date.parse(date);
        var val2 = new Date(val1);
        var str = val2.toLocaleString('en-US');
        return str;
    }

    function handleDelete({ exam_number }) {

        setLoading(true)
        console.warn("Exam-Id or Number>>>", exam_number)
        axios.delete(`https://morning-gorge-09623.herokuapp.com/api/v1/exam/${exam_number}`, {
            crossDomain: true,
            // withCredentials: true,
        headers: {
            'accept': 'application/json',
            "Content-type": "application/json;charset=UTF-8",
            'Access-Control-Allow-Origin': 'http://localhost:3000/',
            'Access-Control-Allow-Methods': 'DELETE',
            'Access-Control-Allow-Headers': 'Content-Type',
            },
            auth: {
                username: JSON.parse(username),
                password: JSON.parse(password)
            }
        }).then(res => {
            console.log(res);
            toast.success(`${exam_number} Deleted Successfully`, { theme: "colored" });
            setTimeout(window.location.reload(true), 2000)
            setLoading(false);
            console.log(res);
        }).catch((error) => {
            setLoading(false)
            toast.error(error.response.data.error_message, { theme: "colored" }); //only if no internet connection 
            toast.error(error.response.data.error, { theme: "colored" }); //only if no internet connection 
            console.error(error)
            console.log(error.message);
        });
    }


    return (<div className='container'>

        <div className='scheduledCont'>
            <div className='head'>
                <Link to={-1}> <FaArrowLeft /> </Link>
                <h2>All Exams Record Data</h2>
            </div>


            <div>
                {loading && <div>Loading...</div>}

                {<div style={{ display: 'block', color: '#fff', background: 'red' }}>{msg && msg}</div>}


                <div>
                    {
                        data && data.map((item) => {
                            return <div className='scheduledList' key={item.id}>

                                <Link to={`/Test/${item.exam_number}`} >
                                    <div className='scheduled' onMouseEnter={() => { console.log("Event:MouseEnter"); setBtnDelete(true); }} >
                                        <div className='courseTitle'>{item.exam_number}</div>
                                        <p>C-No:{item.candidates.length}</p>

                                        <div><strong>{convertDatetoStr(item.created.toString())}</strong></div>
                                    </div>
                                </Link>


                                <div style={{ display: btnDelete ? 'block' : 'none' }}>
                                    <button onClick={() => { handleDelete(item) }}>Delete exam</button>
                                </div>
                            </div>

                        })}
                </div>


            </div>
        </div>


    </div>
    )
}
/*
  <tr>
                    <th>Test/No</th>
                    <th>Name</th>
                    <th>Instructions</th>
                    <th>Description</th>
                    <th>Duration</th>   
                    <th>Pass_mark</th>    
                    <th>Start</th>
                    <th>Created</th>
                    <th>Questions</th>
                    <th>Timed</th>
                </tr>


*/

export default Swagger
