import axios from 'axios';
import React, {useState, useContext, useEffect} from 'react'
import {useParams} from  'react-router-dom'
import { toast } from 'react-toastify';


function Test() {

    const {test_number} = useParams()
   // const {style, setStyle, navigate, txtField} = useContext(DataContext);
   const [msg, setMsg] = useState("");
   const [data, setData]  = useState([]);


   const username = localStorage.getItem('email');
   const password = localStorage.getItem('password');


    const [id, setId] = useState("");
    const [name, setName] = useState("")
    const [pass_mark, setPass_mark] = useState("");
    const [description, setDescription] = useState("");
    const [instructions, setInstructions] = useState("");
    const [start, setStart] = useState("");
    const [duration, setDuration] = useState("");
    const [timed, setTimed] = useState(true)
    const [created, setCreated] = useState(new Date().toISOString());
    const [questions, setQuestions] = useState([])
    const [loading, setLoading] = useState(false);

    const [candidates, setCandidates] = useState([])
    useEffect(()=>{
    axios.get(`https://morning-gorge-09623.herokuapp.com/api/v1/exam/${test_number}`, {
      crossDomain: true,
        // withCredentials: true,
        headers: {
          'accept': 'application/json',
          "Content-type": "application/json;charset=UTF-8",
          'Access-Control-Allow-Origin': 'http://localhost:3000/',
          'Access-Control-Allow-Methods': 'GET',
          'Access-Control-Allow-Headers': 'Content-Type',
      },
      auth: {
        username: username,
        password: password
      }
    })
    .then(response=>{
      // console.log(response)
      console.log("RESPONSE DATA----",response.data)

    })
    .catch(error=>{
      console.log("ERROR---",error)
      console.log("ERROR--MESSAGE",error.message)
      console.log("----------NAME----", error.response.data.name)
      setName(error.response.data.name)
      setDescription(error.response.data.description)
      setInstructions(error.response.data.instructions)
      setPass_mark(error.response.data.pass_mark)
      setTimed(error.response.data.time)
      setDuration(error.response.data.duration)
      setCandidates(error.response.data.candidates)


    })

    },[])

    function handleUpdate(id){
    //  console.log(data)
      const info = {
        test_number,
        id, 
        name, 
        pass_mark, 
        description, 
        instructions, 
        start,
        duration,
        timed,
        questions: questions
    }
    console.log(info)

    const jsonData = JSON.stringify(info)

    axios.put(`https://morning-gorge-09623.herokuapp.com/api/v1/exam/`, jsonData, {
            crossDomain: true,
            // withCredentials: true,
            headers: {
              'accept': 'application/json',
              "Content-type": "application/json;charset=UTF-8",
              'Access-Control-Allow-Origin': 'http://localhost:3000/',
              'Access-Control-Allow-Methods': 'DELETE',
              'Access-Control-Allow-Headers': 'Content-Type',
          },
          auth: {
            username: username,
            password: password
          }
        })
        .then(res => {
            console.log(res);
            // alert(`${exam_number} Deleted Successfully`)
            toast.success(`${test_number} Updated Successfully`, {theme: "colored"}); 
            setLoading(false);
            console.log(res);
        })
        .catch((error)=>{
            setLoading(false)
            setMsg(error.response.data.error);
            setMsg(error.response.data.error_message);
            console.error("-------ERROR-------",error)
            console.log("------ERROR--MSG--",error.message);
        });
    }

    function handleAccept(e){
      e.preventDefault();
      console.log(id);
    }

  return (
    <div>
        <h1>Test - {test_number}</h1>


        <div className='updateForm'>
          <label>Id <input type='text' readOnly value={id} /></label> <br />
          <label>Number: <input type='text' readOnly value={test_number} /></label> <br />

           <label>Name: <input type='text' value={name}
            onChange={(e) => { setName(e.target.value) }} /></label> <br />

            <label>Description: <textarea value={description}
            onChange={(e) => {setDescription(e.target.value)}} /></label>

            <label>Pass_mark: <input type='text' value={pass_mark}
            onChange={(e) => {setPass_mark(e.target.value)}} /></label>

            <label>Instruction: <textarea value={instructions}
            onChange={(e) => {setInstructions(e.target.value)}} /></label>


            <label>Duration: <input type='text' value={duration}
            onChange={(e) => {setDuration(e.target.value)}} /></label>



            <label>Timed: <input type='text' value={timed ? "YES TIME": "NOT TIME"}
            onChange={(e) => {setTimed(e.target.value)}} /></label>

          <button onClick={handleUpdate}>Update</button> 

    <div>
    <h3>candidates</h3>


    <form>
    <table border='1px'>
    <thead>
    <tr>
      <th>Name</th>
      <th>Email</th>
      <th>Accept/Reject</th>
      </tr>

    </thead>
    <tbody>
    {candidates.map((std)=>{
    return<tr key={std.id}>
          <td>{std.name}</td>
          <td>{std.email}</td>
          <td><button onClick={handleAccept}>Accept</button></td>
      </tr>   
    })}
    </tbody>

    </table>

    </form>



    </div>



          </div>








    </div>
  )
}

export default Test
