import axios from 'axios';
import React, { useState, useEffect, useReducer } from 'react'
import { useParams } from 'react-router-dom'
import { toast } from 'react-toastify';


function Test() {

  const { exam_number } = useParams()
  // const {style, setStyle, navigate, txtField} = useContext(DataContext);
  const [msg, setMsg] = useState("");
  const [loading, setLoading] = useState(false);

  const username = localStorage.getItem('email');
  const password = localStorage.getItem('password');

  const [id, setId] = useState("");
  const [name, setName] = useState("")
  const [pass_mark, setPass_mark] = useState("");
  const [description, setDescription] = useState("");
  const [instructions, setInstructions] = useState("");
  const [start, setStart] = useState("");
  const [duration, setDuration] = useState("");
  const [timed, setTimed] = useState(true);
  const [questions, setQuestions] = useState([]);
  const [candidates, setCandidates] = useState([]);
  const [registered_candidates, setRegistered_candidates] = useState([])
  const url = 'https://morning-gorge-09623.herokuapp.com/api/v1/exam/';


  useEffect(() => {

    const fetchData = async () => {
      try {
        const result = await axios({
          method: 'get',
          url: `${url}${exam_number}`,
          crossDomain: true,
         headers:{
          'accept': 'application/json',
          "Content-type": "application/json;charset=UTF-8",
          'Access-Control-Allow-Origin': 'http://localhost:3000/',
          'Access-Control-Allow-Methods': 'GET',
          'Access-Control-Allow-Headers': 'Content-Type',
         }

        });
        console.log("FETCHING DATA::::::::")
        console.log(result.data)

      } catch (error) {
        console.log(error)
        console.log(error.message)
      }

    }
fetchData()
    /*  axios.get(`${url}${exam_number}`, {
        crossDomain: true,
        // withCredentials: true,
        headers: {
          'accept': 'application/json',
          "Content-type": "application/json;charset=UTF-8",
          'Access-Control-Allow-Origin': 'http://localhost:3000/',
          'Access-Control-Allow-Methods': 'GET',
          'Access-Control-Allow-Headers': 'Content-Type',
        },
        auth: {
          username: JSON.parse(username),
          password: JSON.parse(password)
        }
      })
        .then(response => {
          console.log(response.data)
          // console.log("RESPONSE DATA----",response.data);
          setId(JSON.stringify(response.data.id))
          setName(response.data.name);
          setDescription(response.data.description)
          setPass_mark(response.data.pass_mark)
          setInstructions(response.data.instructions)
          setDuration(response.data.duration)
          setStart(response.data.start)
          setCandidates(response.data.candidates)
          setQuestions(response.data.questions)
          setRegistered_candidates(response.data.registered_candidates)
        })
        .catch(error => {
          console.log("ERROR---", error)
          console.log("ERROR--MESSAGE", error.message)
        })
  */
  }, [])

  function handleUpdate(id) {
    //  console.log(data)

    const info = {
      exam_number,
      name,
      pass_mark,
      description,
      instructions,
      start,
      duration,
      timed,
      questions,
    }
    console.log("INFO", info)
    const jsonData = JSON.stringify(info)
    console.log("JSONDATA", jsonData)

    axios.put(`https://morning-gorge-09623.herokuapp.com/api/v1/exam/`, jsonData, {
      crossDomain: true,
      // withCredentials: true,
      headers: {
        'accept': 'application/json',
        "Content-type": "application/json;charset=UTF-8",
        'Access-Control-Allow-Origin': 'http://localhost:3000/',
        'Access-Control-Allow-Methods': 'DELETE',
        'Access-Control-Allow-Headers': 'Content-Type',
      },
      auth: {
        username: username,
        password: password
      }
    })
      .then(res => {
        // console.log(res);
        toast.success(`${exam_number} Updated Successfully`, { theme: "colored" });
        setLoading(false);
        console.log(res);
      })
      .catch((error) => {
        setLoading(false)
        setMsg(error.response.data.error);
        setMsg(error.response.data.error_message);
        console.error("-------ERROR-------", error)
        console.log("------ERROR--MSG--", error.message);
      });


  }



  function handleAccept({ email, name, id }) {
    const data = new Array(email);
    const jsonData = JSON.stringify(data)
    console.log("EMAIL --------->", jsonData)

    axios.patch(`${url}${exam_number}/candidates/approve`, jsonData, {
      headers: {
        'accept': 'application/json',
        "Content-type": "application/json;charset=UTF-8",
        'Access-Control-Allow-Origin': 'http://localhost:3000/',
        'Access-Control-Allow-Methods': 'PATCH',
        'Access-Control-Allow-Headers': 'Content-Type',
      },
      auth: {
        username: JSON.parse(username),
        password: JSON.parse(password)
      }
    }).then((response) => {
      console.log("--------------RESPONSE STATUS-------------", response.status);
      // setCandidates(oldCandidates => [...oldCandidates, response.candidates])
      toast.success(`${name} Added Successfully`, { theme: "colored" })

    })
      .catch((error) => {
        console.log(error.message)
        toast.error(`${error.message}`);
        toast.error(` Failed to ${name}`, { theme: "colored" })
      })



  }


  return (
    <div className='container'>

      <div className='updateForm' style={{ display: 'none' }}>
        <h2>Exam Name - {exam_number}, {questions && questions.length} Questions</h2>


        <label>Id: <input type='text' readOnly value={id} onChange={(e) => setId(e.target.value)} /></label>
        <label>Exam Name: <input type='text' readOnly value={exam_number} /></label>

        <label>Name: <input type='text' value={name}
          onChange={(e) => { setName(e.target.value) }} /></label>

        <label>Description: <textarea value={description}
          onChange={(e) => { setDescription(e.target.value) }} /></label>

        <label>Pass_mark: <input type='text' value={pass_mark}
          onChange={(e) => { setPass_mark(e.target.value) }} /></label>

        <label>Instruction: <textarea value={instructions}
          onChange={(e) => { setInstructions(e.target.value) }} /></label>


        <label>Duration: <input type='text' value={duration}
          onChange={(e) => { setDuration(e.target.value) }} /></label>

        <label>Date: <input type='date' value={start && new Date(start).toISOString().slice(0, 10)}
          onChange={(e) => { setStart(e.target.value) }} /></label>


        <label>Timed: <input type='text' value={timed ? "YES TIME" : "NOT TIME"}
          onChange={(e) => { setTimed(e.target.value) }} /></label>

        <button onClick={handleUpdate}>Update</button>

      </div>


      <div className='accept_table'>
        <h3>New Registered Candidates</h3>

        <table border='1px'>
          <thead>
            <tr>
              <th>Name</th>
              <th>Email</th>
              <th>Accept</th>
            </tr>

          </thead>
          <tbody>
            {registered_candidates.map((std) => {
              return <tr key={std.id}>
                <td>{std.name}</td>
                <td>{std.email}</td>
                <td><button onClick={() => { handleAccept(std) }}>Accept</button></td>
              </tr>
            })}
          </tbody>

        </table>
      </div>

      <div className='accept_table'>
        <h3>Approved Candidates</h3>
        <table border='1px'>
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Email</th>
            </tr>

          </thead>
          <tbody>

            {candidates.map((std) => {
              return <tr key={std.id}>
                <td>{std.id}</td>
                <td>{std.name}</td>
                <td>{std.email}</td>
              </tr>
            })}

          </tbody>

        </table>

      </div>



    </div>
  )
}

