import axios from 'axios';
import React, { useState } from 'react'
import { Link } from 'react-router-dom';

function GetOneExam() {

    const [examnumber, setExamnumber] = useState("");
    const [loading, setLoading] = useState(false);
    const [msg, setMsg] = useState("");
    const  [data, setData] = useState([]);

    function convertDatetoStr(info){
        var date = info;
        var val1 = Date.parse(date);
        var val2 = new Date(val1);
        var str = val2.toLocaleString('en-US');
        return str;
    }

    function handleFind({exam_number}){
       console.log(examnumber)

        axios.get(`https://morning-gorge-09623.herokuapp.com/api/v1/exam/${exam_number}`, {
            crossDomain: true,
            // withCredentials: true,
            headers: {
              'accept': 'application/json',
              "Content-type": "application/json;charset=UTF-8",
              'Access-Control-Allow-Origin': 'http://localhost:3000/',
              'Access-Control-Allow-Methods': 'GET',
              'Access-Control-Allow-Headers': 'Content-Type',
          },
          auth: {
            username: 'mujtaba@gmail.com',
            password: '123456'
          }
        })
            .then(res => {
                console.log(res);
                setLoading(false);
                console.log(res);

            })
            .catch((error)=>{
                setLoading(false)
                setMsg(error.response.data.error);
                console.error(error)
                console.log(error.message);
            });  

    }


  return (
    <div className='container'>
        <h3>Get Single Exam by Unique ID</h3>

        <form onSubmit={handleFind}>
            <label>Exam Number:
                <input type='text'
                    placeholder='Exam Number' 
                    value={examnumber.toUpperCase()}
                    onChange={(e)=>setExamnumber(e.target.value)}
                 /></label>
            <button>Find</button>
        </form>

        <div>
             {loading && <div>Loading...</div>}
             { <div style={{display:'block', color:'#fff', background: 'red'}}>{msg && msg}</div>}

                {
                   data && data.map((item)=>{  
                        return<div className='scheduledList' key={item.id}>

                            <Link to={`/Test/${item.exam_number}`} >
                                <div className='scheduled' onMouseEnter={() => { console.log("Event:MouseEnter"); setBtnDelete(true); }} >
                                    <div className='courseTitle'>{item.exam_number}</div>
                                   <div><strong>{convertDatetoStr(item.created.toString()) }</strong></div>
                                </div>
                            </Link>

                                <div  style={{display: btnDelete ? 'block' : 'none'}}>
                                    <button onClick={()=>{handleDelete(item)}}>Delete exam</button> 
                                </div>
                            </div>

                 }) }
    </div>
</div>
  )
}

export default GetOneExam
