import styled from "styled-components";

export const Container = styled.div`
    /* border:1px solid red; */
    width:90%;
    margin:auto;
`;
export const Input = styled.input`
    width: 100%;
    margin:5px auto;
    padding: 10px;
    font-size: 0.7rem; 
    transition:.4s ;

    /* ------- DEKTOP VIEW --------*/
    @media screen and (min-width:600px){
        font-size: 1rem; 
        padding: 10px;
    } 
`

export const Button = styled.button`
    background:#1b1bf0;
    width:100%;
    padding: 10px;
    border:none;
    color: #fff;
    font-weight:bold;

`;

export const LoginForm = styled.div`
    display:flex;
    flex-direction:column;
    margin:20px auto;
    form{
        margin:20px auto;
    }

    /* ------- DEKTOP VIEW --------*/
    @media screen and (min-width:600px){
        width:500px;
    } 

`;
