import axios from 'axios';
import React, { useContext, useState } from 'react'
import { Link } from 'react-router-dom';
import { toast } from 'react-toastify';
import { DataContext } from '../../Context/DataContext';
import { Container, Input, LoginForm } from '../../Styled';


function AdminLogin() {

  const { navigate } = useContext(DataContext);

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);
  const [msg, setMsg] = useState(false);
  const [name, setName] = useState("");
  const [submitForm, setSubmitForm] = useState()
  const [valid, setValid] = useState(false);


  const handleSubmit = (e) => {
    e.preventDefault();
    const data = { email, password, name }
    const jsonData = JSON.stringify(data)



    if(name && password && email){
     alert('submiteed')
    }
    setSubmitForm(true); 
  }

  return (
    <Container>
      <LoginForm>
        <div>
          <h3>Admin Login. </h3>
          {loading && <p>Loading...</p>}
          {msg && <p>{msg}</p>}
        </div>

        <div>
          <form onSubmit={handleSubmit}>
            <label>Name {submitForm && !name ? <span style={{color:'red'}}>Name Filed Can Not Blank</span>: null}
              <Input type='text' value={name} onChange={(e) => setName(e.target.value)} />
            </label>

            <label>Email  
            {submitForm && !email ? <span style={{color:'red'}}>Email Filed Can Not Blank</span>:null}
            {submitForm && !email.includes('@') ? <span style={{color:'red'}}>Email is not Valite</span>:null}


            <Input type='text' value={email} onChange={(e) => setEmail(e.target.value)} /></label>

            <label>Password  {submitForm && !password ? <span style={{color:'red'}}>Password Filed Can Not Blank</span>: null}
             <Input type='password' value={password} onChange={(e) => setPassword(e.target.value)} /></label>
            <button>Submit</button>
          </form>
        </div>
      </LoginForm>
      <Link to='/admin'> Login</Link>

    </Container>
  )
}

export default AdminLogin
