    const [start, setStart] = useState("");
    const [dataTime, setDataTime] = useState("");
    const [duration, setDuration] = useState("");
    const [timed, setTimed] = useState(true)
    const [created, setCreated] = useState(new Date().toISOString());
    const [questions, setQuestions] = useState([])
    const [loading, setLoading] = useState(false);

    useEffect(()=>{
       fetch(`https://enigmatic-cove-89902.herokuapp.com/api/v1/test/${test_number}`)
        .then((response)=>{
            const result = response 
            return result.json()
            // setData(result);

        }).then((results)=>{
            console.log(results);
            setData(results); 
            setName(results.name);
            setDescription(results.description)
            setPass_mark(results.pass_mark)
            setDuration(results.duration)
            setTimed(results.timed)

        }).catch((error)=>{
            console.log(error.message)
        })
    }, [test_number])

    function handleUpdate(id){

      const info = {
        test_number, 
        name, 
        pass_mark, 
        description, 
        instructions, 
        start,
        duration,
        timed,
        questions
      }
      console.log(info)


