import axios from 'axios';
import React, {useContext, useState} from 'react'
import { Link } from 'react-router-dom';
import { toast } from 'react-toastify';
import { DataContext } from '../../Context/DataContext';


function AdminLogin() {

    const {navigate} = useContext(DataContext);


    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [loading, setLoading] = useState(false);
    const [msg, setMsg] = useState(false);
    const [name, setName] = useState("");


    const handleSubmit = (e) =>{
        e.preventDefault();
        const data = {email, password, name}
        const jsonData = JSON.stringify(data)

        // axios.post('https://morning-gorge-09623.herokuapp.com/api/v1/auth/register', jsonData,
        // {headers:{
        //   'Accept': 'application/json, text/plain',
        //   'Content-Type': 'application/json;charset=UTF-8'
        // }} )
        // .then((response)=>{
        //   console.log("RESPONSE",response);
        //   console.log("RESPONSE:::DATA",response.data);
        //   console.log("RESPONSE:::STATUS",response.status);
        //   if(response.status === '201'){
        //     setMsg("New User Create Successfully.")  
        //   }
        //   setLoading(false)
        //   setMsg(response.status)
        // })
        // .catch((error)=>{
        //   console.log(error)
        //   console.log("Error Message:::",error.message)
        //   console.log("Error in Axios:::",error.response.data.error_message)
        //   setLoading(false)
        //   setMsg(error.response.data.error_message)
        // })
        // setAuth(data && data)
        console.log(data)
        localStorage.setItem('name', data.name);
        localStorage.setItem('email', data.email);
        localStorage.setItem('password', data.password);
        toast.success("Login Successfully", {theme: "colored"});
        navigate('/admin');  
      }
      function convertDatetoStr(info){
        var date = info;
        var val1 = Date.parse(date);
        var val2 = new Date(val1);
        var str = val2.toLocaleString('en-US');
        return str;
    }

  return (
    <div className='container'>
    <div className='admin'>
      <div>
          <h3>Admin Login. </h3>  
          {loading && <p>Loading...</p>}
          {msg && <p>{msg}</p>}
      </div>

      <div>
      <h3>Date</h3>
      <input type='text' />
      </div>


      <div>
          <form onSubmit={handleSubmit}>
            <label>Name <input type='text' value={name} onChange={(e)=>setName(e.target.value)} /></label>
            <label>Email <input type='email' value={email} onChange={(e)=>setEmail(e.target.value)} /></label>
            <label>Password <input type='password' value={password} onChange={(e)=>setPassword(e.target.value)} /></label>
            <button>Submit</button>
          </form> 
     </div>
    </div>
      <Link to='/admin'> Login</Link>

    </div>
  )
}

export default AdminLogin
