import React, {useContext, useState} from 'react'
import DataProvider from '../Context/DataContext';

function QuestionForm({record, dataTime }) {

  //   Questions Variables.......
  const [point, setPoint] = useState("");
  const [text, setText] = useState(); //quetion var txt 
  const [answer, setAnswer] = useState(); //radio button tragger and set the answer
  const [optA, setOptA] = useState("");
  const [optB, setOptB] = useState("");
  const [optC, setOptC] = useState("");
  const [optD, setOptD] = useState("");

  const [questions, setQuestions] = useState([]); //Array of question

  function handleAddQuestion(e){
    e.preventDefault();
       const options = [optA, optB, optC, optD]; // option ie answers 
       const id = Math.random();
       const datas = {text, point, options, answer, id}; // object to store descriptions & array of options 
       const listOfQuestions = [datas]; //Array that store question,answer,options,point

       setQuestions([...questions, {
        id:id,
        text: datas.text,
        answer: datas.answer,
        point: datas.point,
        options: datas.options
      }])  

      questions.push(listOfQuestions);

       //   Question Variables....

       setText('');
       setPoint('');
       setAnswer(false);
       setOptA('');
       setOptB('');
       setOptC('');
       setOptD('');

    }

    function handleSubmit(e){
      e.preventDefault();

      const info =  {
          test_number:record.test_number,
          name:record.name,
          pass_mark:record.pass_mark,
          description:record.description, 
          instructions:record.instructions, 
          start:record.start,
          duration: dataTime,
          timed:record.timed,
          questions:questions
      };

      console.log(info);
      console.log(dataTime);
    }

  return (
    <div className='container'>
      <h3>Questions {questions.length}</h3>

      <div className='question_form'>

      <label>Question <textarea name='question'  value={text} onChange={(e)=> setText(e.target.value) } /> </label>
      <label>Points <input type='number' name='point' value={point} onChange={(e)=> setPoint( e.target.value)} /> </label>
      <h4>Options</h4>

      <label><input placeholder='A' type='text' name='A' value={optA} onChange={(e)=> setOptA( e.target.value)} />
      <input type='radio' name='answer' value={optA} onChange={(e)=> setAnswer(e.target.value)} checked={answer === optA} />
      </label> 



      <label><input placeholder='B' type='text' name='B' value={optB} onChange={(e)=> setOptB( e.target.value)} /> 
      <input type='radio' name='answer' value={optB} onChange={(e)=> setAnswer(e.target.value)} checked={answer === optB} /> 
      </label>

      <label><input placeholder='C' type='text' name='C' value={optC} onChange={(e)=> setOptC( e.target.value)} /> 
      <input type='radio' name='answer' value={optC} onChange={(e)=> setAnswer(e.target.value)} checked={answer === optC} />
      </label>  

      <label><input placeholder='D' type='text' name='D' value={optD} onChange={(e)=> setOptD( e.target.value)} /> 
      <input type='radio' name='answer' value={optD} onChange={(e)=> setAnswer(e.target.value)} checked={answer === optD}/> 
      </label>  

        <button onClick={handleAddQuestion}>Add</button>
        <button onClick={()=>{alert('I wa Clicked...')}}>Submit</button>

        </div>
    </div>
  )
}

export default QuestionForm
