import axios from 'axios';
import React, { useState } from 'react'

function CreateUser() {
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [loading, setLoading] = useState(false);


    const handleSubmit = (e) => {
        e.preventDefault()
        setLoading(true)
        const data = {name, email, password}
        const jsonData = JSON.stringify(data);

        axios.post('https://morning-gorge-09623.herokuapp.com/api/v1/auth/register', jsonData,
        {headers:{
          'Accept': 'application/json, text/plain',
          'Content-Type': 'application/json;charset=UTF-8'
        }} )
        .then((response)=>{
          console.log("RESPONSE",response);
          console.log("RESPONSE:::DATA",response.data);
          console.log("RESPONSE:::STATUS",response.status);
        })
        .catch((error)=>{
          console.log(error)
          console.log(error.message)
          setLoading(false)
        })

        console.log(data)
    }

  return (
    <div className='container'>
    <h3>CreateUser</h3>

    <p>Maybe role</p>
    {loading && <p style={{background:'red', color:'#fff'}}>Loading...</p>}
        <form onSubmit={handleSubmit}>
            <label>Name: <input type='text' value={name} onChange={(e)=>setName(e.target.value)} /></label> <br /><br />
            <label>email: <input type='email' value={email} onChange={(e)=>setEmail(e.target.value)} /></label><br /><br />
            <label>password: <input type='password' value={password} onChange={(e)=>setPassword(e.target.value)} /></label><br /><br />
          <button>Create</button>
        </form>

        <h1>name : {name}</h1>
        <h1>email : {email}</h1>
        <h1>password : {password}</h1>

    </div>
  )
}

export default CreateUser
