import React, { useContext, useState } from 'react'
import TestInfo from './Components/TestInfo'
import QuestionForm from './Components/QuestionForm'
import { FaArrowLeft, FaArrowRight } from 'react-icons/fa'
import QuestionSummary from './Components/QuestionSummary'
import { DataContext } from './Context/DataContext'

function DisplayHome() {

  const [page, setPage] = useState(0)
  const Info = ['Test/Exams Info', 'Create Questions'];   

  const {style, setStyle} = useContext(DataContext)


  const [dataTime, setDataTime] = useState("");

  const [record, setRecord] = useState({
    test_number:"",
    name:"",
    pass_mark:"",
    description:"", 
    instructions:"", 
    start:'',
    duration: "",
    timed:'',
    question:[]
});

  const Display = () => {
    if(page === 0){
      return <TestInfo record={record} setRecord={setRecord} dataTime={dataTime} setDataTime={setDataTime}  />
    }else{
      return <QuestionForm record={record}  dataTime={dataTime}  /> 
    } 
  }

  return (
    <div className='container'>

      <h6>{Info[page]}</h6>    
      <div>
        {Display()}
      </div>


      <button style={{display: page === 0 ? 'none': ''}} onClick={()=>{ setPage((pages)=> pages - 1)}} >Prev <FaArrowLeft /> </button>

      <button style={{display: page === Info.length - 1 ? 'none': ''}} onClick={()=>{ setPage((pages)=> pages + 1)}} >Proceed <FaArrowRight /> </button>
      <button style={{display: style ? 'none': ''}} onClick={()=>setStyle(!style)}>Proceed Dis <FaArrowRight /></button>
      {/*
      <button disabled ={page === 0} style={{display:'none'}} onClick={()=>{ setPage((pages)=> pages - 1) }}>Prev</button>
      <button disabled ={page === Info.length - 1} onClick={()=>{ setPage((pages)=> pages + 1) }}>Next</button>
  */}


    </div>
  )
}

export default DisplayHome
