import axios from 'axios'
import React, { useEffect, useState } from 'react'


function TakeTest() {

    const [data, setData] = useState([])
    const [loading, setLoading] = useState(false)
    const [question, setQuestion] = useState([]);
    const [single, SetSingle] = useState([]);
    const [num, setNum] = useState(0)
    const [optA, setOptA] = useState("")
    const [optB, setOptB] = useState([])
    const [answer, setAnswer] = useState();

    useEffect(()=>{
        axios.get('https://enigmatic-cove-89902.herokuapp.com/api/v1/test')
         .then((res)=>{
             // console.log("RESPONSE>>>", res)
             // console.log("RESPONSE::DATA>>>", res.data[1].questions)
             setData(res.data[1].questions[num]);
             setOptA(res.data[1].questions[num].options[0])
             setOptB(res.data[1].questions[num].options[1])

             setLoading(false)
         }).catch((error)=>{
             console.log(error.message);
             setLoading(false);
         })
     }, [num])



    function Next() {
        return setNum(num + 1);
    }

    function Prev() {   
        return setNum(num - 1);
    }

    function HandleSubmit(e){
        e.preventDefault();
        console.log("ANSWER,", answer)

    }


  return (
    <div>
        <h4>Exam Start </h4>

        {loading && <p>loading...</p>}
        <h4>Q({num + 1}) </h4>


        <div>
        <form onSubmit={HandleSubmit}>
            <p>Que: {data.text}</p>

            <p>   <label htmlFor='answer'>{optA}</label> 
            <input  type='radio' 
                    name='answer'
                    id='answer'
                    value={optA} 
                    onChange={(e)=> setAnswer(e.target.value)} 
                    checked={answer === optA}
            />
            </p>  

            <p>   <label htmlFor='answerB'>{optB}</label> 
            <input  type='radio' 
                    name='answer'
                    id='answerB'
                    value={optB} 
                    onChange={(e)=> setAnswer(e.target.value)} 
                    checked={answer === optB}
            />
            </p>      





            <button>Submit</button>
        </form>
        </div>

        <button onClick={()=>{Next()}}>Next</button>
        <button onClick={()=>{Prev()}}>Prev</button>





    </div>
  )
}

export default TakeTest
