import axios from 'axios';
import React, { useState } from 'react'

function CreateUser() {
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState(""); 

    const [loading, setLoading] = useState("");
    const [msg, setMsg] = useState = ("");

    const url = 'red';

    const handleSubmit = (e) => {
        e.preventDefault();
        const data = {name, email, password}
        const jsonData = JSON.stringify(data);

        axios.post(url, jsonData, {
            headers: {
              Accept: 'application/json',
            }
          })
          .then((response) => {
            setLoading(false);
            alert(`New Record Added Successfully`); 
            console.log("RESPONSE",response);
            console.log("RESPONSE:::DATA",response.data);
            console.log("RESPONSE:::STATUS",response.status);
          })
          .catch((error) => {
            setLoading(false)
            setMsg(error.message);
            console.error(error)
            console.log(error.message);
          }) 


        console.log(data)
    }

  return (
    <div>
            <h3> CreateUse</h3> 
            {loading && <p>loading...</p>}
            {msg && <p>{msg}</p>}
            <form onSubmit={handleSubmit}>
            <label>Name:<input type='text' value={name} onChange={(e)=> setName(e.target.value)} />
            </label><br /><br />
            <label>Email:<input type='email' value={email} onChange={(e)=> setEmail(e.target.value)} /> 
            </label><br /><br />
            <label>Password:<input type='password' value={password} onChange={(e)=> setPassword(e.target.value)} />
            </label><br /><br />
            <button>submit</button>

            </form>
    </div>
  )
}

export default CreateUser
