import React, { useContext, useState } from 'react'
import TestInfo from './Components/TestInfo'
import QuestionForm from './Components/QuestionForm'


function DisplayHome() {

  const [page, setPage] = useState(0)
  const Info = ['Test/Exams Info', 'Create Questions', 'Final'];   



  const [record, setRecord] = useState({
    dataTime:"",
    test_number:"",
    name:"",
    pass_mark:"",
    description:"", 
    instructions:"", 
    start:'',
    duration: '',
    timed:'',
    questions:[]
});

  const Display = () => {
    if(page === 0){
      return <TestInfo record={record} setRecord={setRecord}  />
    }else if(page === 1){
      return <QuestionForm record={record} setRecord={setRecord}  />
    }else {
      return <h1>Last Submit Informations</h1>
    }
  }

  return (
    <div>

      <h2>{Info[page]}</h2>    
      <div>
        {Display()}
      </div>

      <button disabled ={page === 0} onClick={()=>{ setPage((pages)=> pages - 1) }}>Prev</button>
      <button disabled ={page === Info.length - 1} onClick={()=>{ setPage((pages)=> pages + 1) }}>Next</button>

    </div>
  )
}

export default DisplayHome
