import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { FaArrowLeft } from 'react-icons/fa';
import {Link, useNavigate} from 'react-router-dom'


function Swagger() {
    const [info, setInfo] = useState([]);
    const [loading, setLoading] = useState(true);
    const [btnDelete, setBtnDelete] = useState(false);
    const navigate = useNavigate();

    const [date, setDate] = useState("");

    useEffect(()=>{
        fetch('https://enigmatic-cove-89902.herokuapp.com/api/v1/test')
        .then((res)=>{
            if(res.ok){
                return res.json()                
            }

        }).then((result)=>{
            // console.log(result);
            setInfo(result);
            setLoading(false);

        })
        .catch((error)=>{
            console.log(error)
        })
    }, [])

    function convertDatetoStr(info){
        var date = info;
        var val1 = Date.parse(date);
        var val2 = new Date(val1);
        var str = val2.toLocaleString('en-US');
        return str;
    }

    function handleDelete({test_number}){


    axios.delete(`https://enigmatic-cove-89902.herokuapp.com/api/v1/test/${test_number}`)
        .then(res => {
            console.log(res);
            console.log('Deleted');
            window.location.reload(true);
        })
        .catch(err => console.log(err));  
    }

  return (<div className='container'>

             <div className='scheduledCont'>

            <Link to={-1}> <FaArrowLeft /> </Link>


                {loading ? <p>Loading...</p> : '' }  
        {
            info.map((item)=>{  
                return<div className='scheduledList' key={item.id}>

                    <Link to={`/Test/${item.test_number}`} >
                        <div className='scheduled' onMouseEnter={() => { console.log("Event:MouseEnter"); setBtnDelete(true); }} >
                            <div className='courseTitle'>{item.test_number}</div>
                            <div><strong>{convertDatetoStr(item.created.toString()) }</strong></div>
                        </div>
                    </Link>

                        <div  style={{display: btnDelete ? 'block' : 'none'}}>
                            <button onClick={()=>{handleDelete(item)}}>Delete exam</button>
                        </div>
                    </div>

                }) }


                </div>


    </div>
  )
}
/*
  <tr>
                    <th>Test/No</th>
                    <th>Name</th>
                    <th>Instructions</th>
                    <th>Description</th>
                    <th>Duration</th>   
                    <th>Pass_mark</th>    
                    <th>Start</th>
                    <th>Created</th>
                    <th>Questions</th>
                    <th>Timed</th>
                </tr>
*/

export default Swagger
